query:
SELECT name as 'Request Number:Link/Request Form:150',file_number as 'File Number:Data:150',full_name as 'Full Name:Read Only:200',date_of_request as 'Date of Request:Date:100', DATEDIFF(NOW(),date_of_request) as 'Overdue:Int:150'
FROM `tabRequest Form`
WHERE DATEDIFF(NOW(),date_of_request) >= 30;
